  padding: 5px;
  border-radius: 15px;
`;

const InputBox = styled.input`
  font-family: "Poppins", sans-serif;
  font-size: 1.2;
  border: 1px solid lightgrey;
  border-radius: 3px;
  padding: 0.5em;
  margin: 0.5rem;
`;
const InputArea = styled.textarea`
  font-family: "Poppins", sans-serif;
  font-size: 1.2;
  border: 1px solid lightgrey;
  border-radius: 3px;
  padding: 0.5em;
  margin: 0.5rem;
  height: 80px;
  width: 100%;
`;
const MainForm = styled.form`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: center;
  padding: 1em;
  background: #f2e2ce;
  border-radius: 3px;
  background: #3f043c;
  width: 250px;
  height: 400px;
  overflow: auto;
  box-sizing: border-box;
  border-radius: 15px;
  margin-right: 50px;
  margin-top: 50px;
  span {
    color: white;
  }
`;
const IngredFormSet = styled.fieldset`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  border: 1px solid #3f043c;
  border-radius: 3px;
  margin: 1em 0;
`;
const BtnClick = styled.button`
  width: 14em;
  height: 3em;
  margin: auto;
  border: 1px solid #3f043c;
  border-radius: 3px;
  background: white;
  color: #d85505;
`;
const Space = styled.p`
  margin: 0.8em;
  padding: 0;
`;
const ListItems = styled.span`
  padding-right: 5px;
`;

const initialState = {
  id: Date.now(),
  name: "",
  image_url: "",
  title: "",
  meal_type: "",
  ingredients: "",
  instructions: "",
  chef_id: ""
}

const AddRecipe = () => {
  const [addRecipe, setAddRecipe] = useState(initialState);

  const handleChange = e => {
    e.preventDefault();
    setAddRecipe({ ...addRecipe, [e.target.name]: e.target.value });
  };
  const onSubmit = e => {
    const {image_url, title, meal_type, ingredients, instructions} = addRecipe
    e.preventDefault();
    if (document.getElementById("meal_type").value != "") {
      axiosAuth()
        .post(`${addRecipe.id}/posts`, {image_url, title, meal_type, ingredients, instructions})
        .then(res => {
          console.log(res.data)
          setAddRecipe(res.data)
        })
        .catch(err => console.log("sorry, can not add a new recipe", err))
    }
  };

  return (
    <form>
    <h3>Add Recipe</h3>
      <input
        type="text"
        name="img_url"
        value={newRecipe.recipe_name}
        placeholder="Title:"
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="source"
        value={newRecipe.source}
        placeholder="Author:"
        onChange={handleChange}

      />

      <button id="submitbutton">Add Recipe!</button>
    </form>
  );
};

export default AddRecipe;
