// Add a form to add a new recipe
import React, { useState, useContext } from "react";
import { axiosAuth } from "/utils/axiosWithAuth";
// import styled from "styled-components";
// import { Context } from "../utils";

const initialState = {
  id: Date.now(),
  name: "",
  image_url: "",
  title: "",
  meal_type: "",
  ingredients: "",
  instructions: "",
  chef_id: ""
}

const AddRecipe = () => {
  const [addRecipe, setAddRecipe] = useState(initialState);

  const handleChange = e => {
    e.preventDefault();
    setAddRecipe({ ...addRecipe, [e.target.name]: e.target.value });
  };
  const onSubmit = e => {
    const {image_url, title, meal_type, ingredients, instructions} = addRecipe
    e.preventDefault();
    if (document.getElementById("meal_type").value != "") {
      axiosAuth()
        .post(`${addRecipe.id}/posts`, {image_url, title, meal_type, ingredients, instructions})
        .then(res => {
          console.log(res.data)
          setAddRecipe(res.data)
        })
        .catch(err => console.log("sorry, can not add a new recipe", err))
    }
  };

  return (
    <form>
    <h3>Add Recipe</h3>
      <input
        type="text"
        name="img_url"
        value={newRecipe.recipe_name}
        placeholder="Title:"
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="source"
        value={newRecipe.source}
        placeholder="Author:"
        onChange={handleChange}

      />

      <button id="submitbutton">Add Recipe!</button>
    </form>
  );
};

export default AddRecipe;
