import React, { useState, useEffect } from "react";
import { axiosWithAuth } from "../utils/axiosWithAuth";
// import AddRecipe from "./AddRecipe";

const initialRecipe = {
    id: "",
    name: "",
    image_url: "",
    title: "",
    meal_type: "",
    ingredients: "",
    instructions: "",
    instructions: "",
    chef_id: ""
}

const ChefPortfolio = props => {
    const [posts, setPosts] = useState([]);
    const [edit, setEdit] = useState(false);
    const [recipeToEdit, setRecipeToEdit] = useState(initialRecipe);

    useEffect(() => {
        axiosWithAuth()
        .get(`/${props.chefId || localStorage.getItem("id")}/posts`)
        .then(res => {
            setPosts(res.data);
            console.log(posts)
        })
        .catch(err => console.log("sorry, an error has occurred while fetching chef portfolio page", err))
    }, []);

    const editRecipe = recipe => {
        setEdit(true);
        setRecipeToEdit(recipe)
    }

    const saveEdit = e => {
        axiosWithAuth()
        console.log(recipeToEdit.name)
        .put(`/posts/${recipeToEdit.id}`, recipeToEdit)
        .then(res => {
        //  setPosts()
        })
        .catch(err => console.log("sorry, could not edit recipe", err))
    };

    return (
        <>
        <h1>Welcome to your Chef Portfolio</h1>
        {/* <AddRecipe/> */}
        <div>
            {posts.map(recipe => (
                <div key={recipe.id} className="recipes">
                    <p>{recipe.name}</p>
                    <p>{recipe.image_url}</p>
                    <p>{recipe.title}</p>
                    <p>{recipe.meal_type}</p>
                    <p>{recipe.ingredients}</p>
                    <p>{recipe.instructions}</p>
                    <p>{recipe.chef_id}</p>
                    <button onClick={() => editRecipe(recipe)}>Edit</button>
                    <button type="submit">Delete</button>
                    <hr/>
                </div>
            ))}

            {edit && (
                <form onSubmit={saveEdit}>
                    <h3>Edit Recipe</h3>
                    <input
                    onChange={e => setRecipeToEdit({ ...recipeToEdit, name: e.target.value })}
                    value={recipeToEdit.name}
                    />
                    <button type="submit">save</button>
                </form>
            )}
        </div>
        </>
    );
};

export default ChefPortfolio;
